static BRACKET_MAPPINGS: phf::Map<char, char> = phf_map! {
    '[' => ']',
    '(' => ')',
    '{' => '}',
    '<' => '>',
};

fn part1() {
    let x: Vec<i32> = INPUT.lines()
        .map(|l| find(l.chars()))
        .filter(|ic| *ic != ' ')
        .map(|c| match c {
            ')' => 3,
            ']' => 57,
            '}' => 1197,
            '>' => 25137,
            _ => panic!()
        })
        .collect();

    println!("{:?}", x.iter().sum::<i32>());
}

fn find(chunk: Chars) -> char {
    let mut stack = Vec::new();

    let mut illegal_char: char = ' ';

    for x in chunk {
        println!("x: {} --- stack: {:?}", x, stack);

        if vec!['(', '[', '{', '<'].contains(&x) {
            stack.push(x);
        } else if *BRACKET_MAPPINGS.get(stack.last().unwrap_or(&' ')).unwrap_or(&' ') == x {
            stack.pop();
        } else {
            illegal_char = x;
            break;
        }
    }

    illegal_char
}

fn part2() {}

fn main() {
    part1();
}